        this.capacity = capacity;
        arr = new int[capacity];
        front = rear = size = 0;
    }
    public boolean enqueue(int value) {
        if (size == capacity) return false;
        arr[rear] = value;
        rear = (rear + 1) % capacity;
        size++;
        return true;
    }
    public int dequeue() {
        if (size == 0) return -1;
        int val = arr[front];
        front = (front + 1) % capacity;
        size--;
        return val;
    }
}

