class MaxHeap {
    private List<Integer> heap;

    public MaxHeap() {
        heap = new ArrayList<>();
    }

    public void insert(int value) {
        heap.add(value);
        int index = heap.size() - 1;
        while (index > 0 && heap.get(index) > heap.get((index - 1) / 2)) {
            Collections.swap(heap, index, (index - 1) / 2);
            index = (index - 1) / 2;
        }
    }

    public int extractMax() {
        if (heap.isEmpty()) return -1;
        int max = heap.get(0);
        heap.set(0, heap.remove(heap.size() - 1));
        maxHeapify(0);
        return max;
    }

    private void maxHeapify(int index) {
        int left = 2 * index + 1, right = 2 * index + 2;
        int largest = index;
        if (left < heap.size() && heap.get(left) > heap.get(largest)) largest = left;
        if (right < heap.size() && heap.get(right) > heap.get(largest)) largest = right;
        if (largest != index) {
            Collections.swap(heap, index, largest);
            maxHeapify(largest);
        }
    }
}

