        Set<Integer> visited = new HashSet<>();
        Set<Integer> recStack = new HashSet<>();

        for (int node : adjList.keySet()) {
            if (!visited.contains(node)) {
                if (detectCycleHelper(node, visited, recStack)) {
                    return true;
                }
            }
        }
        return false;
    }

