        visited.add(node);
        recStack.add(node);
        for (int neighbor : adjList.getOrDefault(node, Collections.emptyList())) {
            if (!visited.contains(neighbor) && detectCycleHelper(neighbor, visited, recStack)) return true;
            if (recStack.contains(neighbor)) return true;
        }
        recStack.remove(node);
        return false;
    }
}

