function watermill(func, limit) {
    let queue = [];
    let activeCount = 0;
    function process() {
        if (queue.length === 0 || activeCount >= limit) return;
        activeCount++;
        let { resolve, reject, args } = queue.shift();
        func(...args).then(resolve).catch(reject).finally(() => {
            activeCount--;
            process();
        });
    }
    return function(...args) {
        return new Promise((resolve, reject) => {
            queue.push({ resolve, reject, args });
            process();
        });
    };
}

function throttle(func, limit) {
    let lastFunc,
        lastRan = 0;
    return function() {
        const context = this,
            args = arguments;
        if (!lastRan) {
            func.apply(context, args);
            lastRan = Date.now();
        } else {
            clearTimeout(lastFunc);
            lastFunc = setTimeout(function() {
                if (Date.now() - lastRan >= limit) {
                    func.apply(context, args);
                    lastRan = Date.now();
                }
            }, limit - (Date.now() - lastRan));
        }
    };
}

