    let cameFrom = {};
    while (openList.length > 0) {
        let current = openList.reduce((lowest, node) => fScores[node] < fScores[lowest] ? node : lowest, openList[0]);
        if (current === end) {
            let path = [];
            while (current) {
                path.unshift(current);
                current = cameFrom[current];
            }
            return path;
        }
        openList = openList.filter(node => node !== current);
        closedList.push(current);
        for (let neighbor of getNeighbors(current, grid)) {
            if (closedList.includes(neighbor)) continue;
            let tentativeGScore = gScores[current] + distance(current, neighbor);
            if (!openList.includes(neighbor)) openList.push(neighbor);
            else if (tentativeGScore >= gScores[neighbor]) continue;
            cameFrom[neighbor] = current;
            gScores[neighbor] = tentativeGScore;
            fScores[neighbor] = gScores[neighbor] + heuristic(neighbor, end);
        }
    }
    return [];
}
